<template>
  <div>
    <h1>{{mensaje}}</h1>
    <p v-html="textohtml" ></p>
    <h4>{{textoinput}}</h4>
    <input type="text" v-model="textoinput"><br><br>
    <img :src="imagen" alt="" @click="sexo()">
  </div>
  <br>
  <br>
  <button @click="basta()">      
    {{bastaYa}}
  </button>
  <br>
  <br>
  <p>{{sex}}</p>
</template>

<script>
export default {
  name: "App",
  data(){
    return{ 
        mensaje:"Prueba de página con vue",
        textohtml: "<i>esta es una prueba de texto en <b>HTML</i></b>",
        textoinput: "Este texto se modifica :v",
        imagen: "data:image/jpeg;base64,",
        bastaYa: "No me des click",
        sex: ""
    }
  },
  methods: {
    basta(){
        switch(Math.floor(Math.random()*4)){
        case 1: 
          this.bastaYa ="Basta ya"
          break;
        case 2: 
          this.bastaYa="Enserio, no es divertido"
          break;
        default:
          this.bastaYa="Atrás pervertido"
      }
    },
    sexo(){
      this.sex+="Sexo "
    }
  }
}
</script>

